import React, { useState } from 'react';
import Stepper from './components/Stepper';
import MainSteps from './components/MainSteps';
import OnboardingStepper from './components/OnboardingStepper';
import PersonalInfoForm from './components/PersonalInfoForm';
import Academic from './components/Academic';
import Documents from './components/Documents';
import Review from './components/Review';
import Complete from './components/Complete';
import "./App.css";

function App() {
  const [currentStep, setCurrentStep] = useState(1);

  return (
    <div className='outer-container'>
      <Stepper currentStep={currentStep} />

      {currentStep === 1 && (
        <>
          <MainSteps />
          <OnboardingStepper />
          <div className='button'>
            <button
              className='button-submit'
              onClick={() => setCurrentStep(2)}
            >
              Get Started <span className="arrow">â†’</span>
            </button>
          </div>
        </>
      )}

      {currentStep === 2 && (
        <PersonalInfoForm setCurrentStep={setCurrentStep} />
      )}

      {currentStep === 3 && (
        <Academic setCurrentStep={setCurrentStep} />
      )}
      {currentStep === 4 && (
        <Documents setCurrentStep={setCurrentStep} />
      )}

       {currentStep === 5 && (
  <Review setCurrentStep={setCurrentStep} />
)}

{currentStep === 6 && (
  <Complete />
)}
    </div>
  );
}

export default App;
