import React from 'react';
import './Review.css';

const Review = ({ setCurrentStep, formData = {} }) => {
  const handleBack = () => {
    setCurrentStep(4); // Go back to Documents page
  };

  const handleSubmit = () => {
    setCurrentStep(6); // Proceed to Complete page
  };

  // Safe data access with fallbacks
  const academicData = {
    institute: formData.institute || 'Not provided',
    course: formData.course || 'Not provided',
    year: formData.year || 'Not provided'
  };

  const documentData = {
    idProof: formData.idProof ? 'Uploaded' : 'Not uploaded',
    marksheet: formData.marksheet ? 'Uploaded' : 'Not uploaded'
  };

  return (
    <div className="review-container">
      <h1>Academic Information</h1>

      <div className="academic-info">
        <p><strong>Institute:</strong> {academicData.institute}</p>
        <p><strong>Course:</strong> {academicData.course}</p>
        <p><strong>Year:</strong> {academicData.year}</p>
      </div>

      <div className="documents-section">
        <h2>Documents</h2>
        <table className="documents-table">
          <tbody>
            <tr>
              <td><strong>ID Proof:</strong></td>
              <td>{documentData.idProof}</td>
            </tr>
            <tr>
              <td><strong>Marksheet:</strong></td>
              <td>{documentData.marksheet}</td>
            </tr>
          </tbody>
        </table>
      </div>

      <div className="review-actions">
        <button
          type="button"
          className="back-button"
          onClick={handleBack}
        >
          ‚Üê Back
        </button>
        <button
          type="button"
          className="submit-button"
          onClick={handleSubmit}
        >
          Submit Application
        </button>
      </div>
    </div>
  );
};

export default Review;
