import React from 'react';
import './Review.css';

const Review = ({ setCurrentStep, formData }) => {
  const handleBack = () => {
    setCurrentStep(4); // Go back to Documents page
  };

  const handleSubmit = () => {
    setCurrentStep(6); // Proceed to Complete page
  };

  return (
    <div className="review-page-container">
      <div className="review-content">
        <h1>Academic Information</h1>

        <div className="academic-info">
          <p><strong>Institute:</strong> {formData.institute || 'Not provided'}</p>
          <p><strong>Course:</strong> {formData.course || 'Not provided'}</p>
          <p><strong>Year:</strong> {formData.year || 'Not provided'}</p>
        </div>

        <div className="documents-section">
          <h2>Documents</h2>
          <table className="documents-table">
            <tbody>
              <tr>
                <td><strong>ID Proof:</strong></td>
                <td>{formData.idProof ? formData.idProof.name : 'Not uploaded'}</td>
              </tr>
              <tr>
                <td><strong>Marksheet:</strong></td>
                <td>{formData.marksheet ? formData.marksheet.name : 'Not uploaded'}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>

      <div className="review-actions">
        <button
          type="button"
          className="back-button"
          onClick={handleBack}
        >
          ‚Üê Back
        </button>
        <button
          type="button"
          className="submit-button"
          onClick={handleSubmit}
        >
          Submit Application
        </button>
      </div>
    </div>
  );
};

export default Review;
