import React from 'react';
import './Review.css';

const Review = ({ setCurrentStep }) => {
  const handleBack = () => {
    setCurrentStep(4); // Go back to Documents page
  };

  const handleSubmit = () => {
    setCurrentStep(6); // Proceed to Complete page
  };

  return (
    <div className="review-container">
      <h1>Review Your Information</h1>
      <p className="subtitle">Please verify all details before submission</p>

      <div className="review-sections">
        {/* Personal Information Section */}
        <div className="review-section">
          <h2>Personal Information</h2>
          <div className="review-item">
            <span className="review-label">Full Name:</span>
            <span className="review-value">John Doe</span>
          </div>
          <div className="review-item">
            <span className="review-label">Email:</span>
            <span className="review-value">john@example.com</span>
          </div>
          {/* Add more personal info fields as needed */}
        </div>

        {/* Academic Information Section */}
        <div className="review-section">
          <h2>Academic Information</h2>
          <div className="review-item">
            <span className="review-label">Institute:</span>
            <span className="review-value">ABC University</span>
          </div>
          <div className="review-item">
            <span className="review-label">Course:</span>
            <span className="review-value">Computer Science</span>
          </div>
          {/* Add more academic info fields as needed */}
        </div>

        {/* Documents Section */}
        <div className="review-section">
          <h2>Documents</h2>
          <div className="review-item">
            <span className="review-label">ID Proof:</span>
            <span className="review-value">uploaded.pdf</span>
          </div>
          <div className="review-item">
            <span className="review-label">Marksheet:</span>
            <span className="review-value">uploaded.pdf</span>
          </div>
          {/* Add more document fields as needed */}
        </div>
      </div>

      <div className="form-actions">
        <button 
          type="button" 
          className="back-button"
          onClick={handleBack}
        >
          <span className="arrow">←</span> Back
        </button>
        <button 
          type="button" 
          className="submit-button"
          onClick={handleSubmit}
        >
          Submit Application <span className="arrow">→</span>
        </button>
      </div>
    </div>
  );
};

export default Review;
